import React, { useState, useEffect } from 'react';

const ChatBot = () => {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');
  const [socket, setSocket] = useState(null);

  useEffect(() => {
    // Replace 'YOUR_WEBSOCKET_URL' with the actual WebSocket URL of your API endpoint
    const apiUrl = 'YOUR_WEBSOCKET_URL';
    const newSocket = new WebSocket(apiUrl);
    setSocket(newSocket);

    // Clean up the WebSocket connection on component unmount
    return () => {
      newSocket.close();
    };
  }, []);

  useEffect(() => {
    if (socket) {
      socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        setResponse(data.message);
      };

      socket.onerror = (event) => {
        console.error('WebSocket error:', event);
      };
    }
  }, [socket]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!socket || socket.readyState !== WebSocket.OPEN) {
      console.error('WebSocket connection is not open.');
      return;
    }

    const payload = {
      message: message,
      newSession: false, // Set to true if you want to start a new session
    };

    socket.send(JSON.stringify(payload));
  };

  return (
    <div>
      <h1>Chat with GPT-3</h1>
      <div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type your message..."
          />
          <button type="submit">Send</button>
        </form>
        {response && <div>Bot: {response}</div>}
      </div>
    </div>
  );
};

export default ChatBot;
