import React, { useState } from 'react';

const ChatBot = () => {
  const [message, setMessage] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Replace 'YOUR_API_URL' with the actual URL of your API endpoint
    const apiUrl = 'https://localhost:7044/';

    // API request payload
    const payload = {
      message: message,
      newSession: false, // Set to true if you want to start a new session
    };

    try {
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      setResponse(data.message);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div>
      <h1>Chat with GPT-3</h1>
      <div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Type your message..."
          />
          <button type="submit">Send</button>
        </form>
        {response && <div>Bot: {response}</div>}
      </div>
    </div>
  );
};

export default ChatBot;
